import React,{useState,useEffect} from 'react'

export const Location = () => {
  const[city,setCity] = useState(null);
  const[search,setSearch]=useState("kathmandu");
  useEffect(() => {
    const fetchApi=async() =>{
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=b3e379e471a4e9f2f5754c62500cf6ed`;
      const response = await fetch(url);
      const resJson=await response.json();
      setCity(resJson.main);
    }
    fetchApi();
  },[search])
  return (
    <div className="container">
    <div className="box">
    
    <div className='weather-forcast'>
    <input type="search" name="city" onChange={(event)=>{setSearch(event.target.value)}}/>
  
    </div>
    {!city ? (
      <p>No Data Found</p>
    ) : ( 
    <>
    <div className="result">
    <h2 className='location'>
      {search}
    </h2>
    <h1 className='temp'>
      {city.temp}
    </h1>
    <p>Min:{city.temp_min} || Max:{city.temp_max}</p>
  </div>
  </>
  )}
   </div>
   </div>
  )
}
