import React,{useState,useEffect} from 'react'

export const Location = () => {
  const[city,setCity] = useState(null);
  const[search,setSearch]=useState("kathmandu");
  // useEffect(() => {
  //   const fetchApi_=async() =>{
  //     const url = 'http://api.openweathermap.org/geo/1.0/direct?q=${search}&limit=5&appid=b3e379e471a4e9f2f5754c62500cf6ed'
  //     const response = await fetch(url);
  //     console.log(response);
  //   }
  //   fetchApi_();
  // });
  useEffect(() => {
    const fetchApi=async() =>{
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=b3e379e471a4e9f2f5754c62500cf6ed`;
      const response = await fetch(url);
      const resJson=await response.json();
      // console.log(resJson);
      setCity(resJson);
    }
    fetchApi();
  },[]);
  return (
    <div className="box">
    
    <div className='weather-forcast'>
    <input type="search" name="city" onChange={(event)=>{setSearch(event.target.value)}}/>
    <button type="submit">Submit</button>
    </div>
    <div className="result">
      <h2 className='location'>
        {search}
      </h2>
      <h1 className='temp'>
        {city.main}
      </h1>
      <p>Min:5.25 Cel || Max:5.2</p>
    </div>
    </div>
  )
}
